import React, { Component } from 'react'
// 通用样式
import './index.css'
import Header from './components/Header'
import List from './components/List'
import Footer from './components/Footer'

export default class App extends Component {
  // 初始化状态
  // 状态在哪，操作状态的函数就在哪
  state = {todos:[
    {id:'001',name:'吃饭',done:true},
    {id:'002',name:'睡觉',done:false},
    {id:'003',name:'打豆豆',done:true},
    {id:'004',name:'逛街',done:false},
    {id:'005',name:'写代码',done:true},
  ]}
  // 保存新的todos
  saveTodos = (todoObj) => {
    // 获取原来的状态
    const {todos} = this.state
    // 创建新的状态
    const newTodos = todoObj
    // 更新状态
    this.setState({todos:[newTodos,...todos]})
  }
  // 确认todos的状态
  updateTodos = (id,done) => {
    // 拿到当前的todos,遍历
    const {todos} = this.state
    const newTodos = todos.map((itemObj) => {
      // 遍历 itemObj，更新选中的item
      if(itemObj.id === id) {
        return {...itemObj,done}
      }
      else {
        return {...todos}
      }
    })
    // 更新state状态
    this.setState
    
  }
  render() {
    const {todos} = this.state
    return (
      <div>
        <div className="todo-container">
            <div className="todo-wrap">
              <Header saveTodo={this.saveTodos}/>
              {/* updateTodos 给item使用，List组件只传递 */}
              <List todos={todos} updateTodos={this.updateTodos}/>
              <Footer/>
            </div>
        </div>
      </div>
    )
  }
}
