import React, { Component } from 'react'
import './index.css'
export default class Item extends Component {
  // 初始化状态
  state = {
    mouse : false
  }
  
  // 鼠标是否移入
  mouseState = (flag) => {
    return () => {
      // 更新状态
      this.setState({mouse:flag})
    }
  }
  // 是否选中,拿到当前id和状态
  selectState = (id) => {
    const {updateTodos} = this.props
    return (event) => {
      console.log(id,event.target.checked);
      
    }
  }
  render() {
    const {id,name,done} = this.props
    const {mouse} = this.state
    return (
      <li style={{backgroundColor:mouse?'#ddd':'white'}} onMouseEnter={this.mouseState(true)} onMouseLeave={this.mouseState(false) }>
        <label>
          <input type="checkbox" defaultChecked={done} onChange={this.selectState(id)}/>
          <span>{name}</span>
        </label>
        <button className="btn btn-danger" style={{display:mouse?'block':'none'}}>删除</button>
      </li>
    )
  }
}
